sysauto_script.condition:
//Create answer variable
var answer = true;

//Get today's date and determine whivch day of the week it is
var rightNow = new GlideDateTime();
var dayOfWeek = rightNow.getDayOfWeekLocalTime();
/* If today is Saturday (6) or Sunday (7), set the answer variable to false
so the Run this script fields script doesn't execute.
Monday = 1, Tuesday = 1 etc */
if (dayOfWeek == 6 || dayOfWeek == 7){
	answer = false;
}

// End Script with something that resolved to true or false. Ignore the warning.
answer;

sysauto_script.script:
/* Query the database for NeedIt Task records with Due date field values older than
the current time. Only return NeedIt Task records that do not have a State field value of Closed Complete. */
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',rightNow);
overdueNITasks.addQuery('state','<=', 3);

overdueNITasks.query();

//Writer a log message for each overdue NeedIt task record.
while(overdueNITasks.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
}
